# [567] 字符串的排列
#

# @lc code=start
from collections import defaultdict


class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        need = defaultdict(int)
        for c in s1:
            need[c] += 1
        window = defaultdict(int)

        left = 0
        right = 0

        while right < len(s2):
            c = s2[right]
            right += 1

            if c in need:
                window[c] += 1
                if window == need:
                    return True
            else:
                window = defaultdict(int)
                left = right

            while left < right and window[s2[left]] > need[s2[left]]:
                window[s2[left]] -= 1
                left += 1
                if window == need:
                    return True

        # print(window, need)
        return False

    def test(self, s1="ab", s2="eidabooo"):
        return self.checkInclusion(s1, s2)

    def test1(self):
        return self.test("hello", "ooolleoooleh")

    def test2(self):
        return self.test(
            "",
            "",
        )


